//     document.querySelector('.popup_for_add').classList.add('popup_opened');
// }

// function handleEditAvatarClick() {
//     isEditAvatarPopupOpen = true;
//     // document.querySelector('.popup_for_avatar').classList.add('popup_opened');
// }

export default App;
