    onSubmit(cardId);
  };

  function handleSubmit(e) {
    e.preventDefault();
    handleDeleteClick();
    onClose();
  }

  return (<PopupWithForm
    name="delete"
    onClose={onClose}
    formName="add-form"
    title="Вы уверены?"
    buttonText="Да"
    onSubmit={handleSubmit}
    isOpen={isOpen}
  >
    <fieldset className="form__set">
    </fieldset>
  </PopupWithForm>)
}

export default DeleteCardPopup;
