import { useState, useEffect, useContext, useRef } from 'react';
import { CurrentUserContext } from '../context/CurrentUserContext';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup({ isOpen, onClose, button }) {


    return (
        <PopupWithForm name="add" title="Новое место" button="Создать" isOpen={isOpen} onClose={onClose}>
            <fieldset className="popup__input">
                <label className="popup__label">
                    <input type="text" className="popup__item popup__item_input_name" name="name" placeholder="Название"
                        maxLength="30" minLength="2" required id="title" />
                    <span class="popup__input-error title-error"></span>
                    <span className="popup__input-error title-error"></span>
                </label>
                <label className="popup__label">
                    <input type="url" className="popup__item popup__item_input_info" name="link"
                        placeholder="Ссылка на картинку" required id="link" />
                    <span className="popup__input-error info-error"></span>
                </label>
                <button className={`button popup__button popup__button_for_add`} type="submit">{button || 'Сохранить'}</button>
            </fieldset>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
