  const handleDeleteClick = () => {
    onSubmit(cardId);
  };

  function handleSubmit(e) {
    e.preventDefault();
    handleDeleteClick();
    onClose();
  }

  return (<PopupWithForm
    name={name}
    onClose={onClose}
    title={title}
    buttonText={button}
    onSubmit={handleSubmit}
    isOpen={isOpen}
    popupDelete={popupDelete}
  >
    <fieldset className="form__set">
    </fieldset>
  </PopupWithForm>)
}

